mapper.select:
SELECT a.id AS "id", a.name AS "name", a.telephone AS "telephone", a.master_name AS "masterName", a.master_phone AS "masterPhone", a.address AS "address", a.joint_eshop_code AS jointEshopCode FROM basic_organization a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name like concat('%',#{name},'%')
AND a.master_name like concat('%',#{masterName},'%')
AND a.master_phone like concat('%',#{masterPhone},'%')
AND a.joint_eshop_code like concat('%',#{jointEshopCode},'%')
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

mapper.update:
UPDATE basic_organization SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select * from basic_organization
WHERE 1=1
AND name = #{name}

	AND id <> #{id}

select * from basic_organization
WHERE 1=1
AND joint_eshop_code = #{jointEshopCode}

	AND id <> #{id}

mapper.update:
UPDATE basic_service_city SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE org_id = #{id}

mapper.select:
SELECT count(1) FROM basic_service_station a
WHERE a.org_id = #{id}
AND a.del_flag = '0'
