mapper.select:
SELECT a.id AS "id", a.name AS "name", a.telephone AS "telephone", a.master_name AS "masterName", a.master_phone AS "masterPhone", a.address AS "address"
FROM basic_organization a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name like concat('%',#{name},'%')
AND a.master_name like concat('%',#{masterName},'%')
AND a.master_phone like concat('%',#{masterPhone},'%')
AND a.id in ( SELECT a.org_id FROM basic_organization_eshop a LEFT JOIN basic_gasq_eshop g ON a.eshop_code =g.code where g.name LIKE concat('%',#{eshopNames},'%') )
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

mapper.update:
UPDATE basic_organization SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select * from basic_organization
WHERE 1=1
AND name = #{name}

	AND id <> #{id}

select * from basic_organization
WHERE 1=1


	AND id <> #{id}

SELECT count(1) FROM basic_service_station a
WHERE a.org_id = #{id}
AND a.del_flag = '0'
