mapper.update:
UPDATE ser_item_city SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE ser_item_city SET
del_flag = #{DEL_FLAG_DELETE}
WHERE item_id = #{id}
AND del_flag = #{DEL_FLAG_NORMAL}

mapper.select:
SELECT
a.area_id AS "cityId",
a.area_name AS "cityName"
FROM office_sevice_area_list a

	a.office_id = #{officeId}
	AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT
a.city_id AS "cityId",
a.city_name AS "cityName"
FROM ser_sort_city a

	a.sort_id = #{sortId}
	AND a.del_flag = #{DEL_FLAG_NORMAL}
