mapper.select:
SELECT 

FROM ser_item_commodity_persons a

WHERE a.id = #{id}

SELECT 

FROM ser_item_commodity_persons a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ser_item_commodity_persons a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ser_item_commodity_persons SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
