mapper.select:
SELECT 

FROM ser_item_info a

WHERE a.id = #{id}

SELECT
FROM ser_item_info a LEFT JOIN ser_item_city b on a.id = b.item_id
a.del_flag = #{DEL_FLAG_NORMAL} AND b.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.major_sort= #{majorSort}
AND a.sort_id= #{sortId}
AND b.city_name LIKE '%'||#{city}||'%' '%'+#{city}+'%' concat('%',#{city},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM ser_item_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ser_item_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) FROM ser_item_info
WHERE name = #{name}

	AND sort_id = #{sortId}

AND office_id = #{officeId}
AND del_flag = #{DEL_FLAG_NORMAL}
