mapper.select:
SELECT 

FROM ser_item_info a

WHERE a.id = #{id}

SELECT 

FROM ser_item_info a
LEFT JOIN ser_sort_info s ON a.sort_id = s.id AND s.del_flag = #{DEL_FLAG_NORMAL}

	LEFT JOIN ser_city_scope b ON a.id = b.master_id AND b.del_flag = #{DEL_FLAG_NORMAL}



	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE concat('%',#{name},'%')


		AND a.major_sort= #{majorSort}


		AND a.sort_id= #{sortId}


		AND (b.city_code = #{cityCode} OR a.all_city = 'yes')




		ORDER BY ${page.orderBy}


		ORDER BY a.sort_num ASC

select id,name,unit,type,price from ser_item_info_goods
where item_id=#{reqId}
and del_flag = '0'

SELECT 

FROM ser_item_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ser_item_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) FROM ser_item_info
WHERE name = #{name}
AND org_id = #{orgId}
AND del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
UPDATE ser_item_info SET

	picture_detail = #{pictureDetail},


	sort_num = #{sortNum},

update_by = #{updateBy.id},
update_date = #{updateDate}
WHERE id = #{id}

mapper.select:
select a.id AS value, a.name as label
from ser_sort_info a
where
org_id = #{orgId}
AND  del_flag = #{DEL_FLAG_NORMAL}
