mapper.select:
SELECT 

FROM ser_item_info a

WHERE a.id = #{id}

SELECT 

FROM ser_item_info a

	LEFT JOIN ser_item_city b ON a.id = b.item_id AND b.del_flag = #{DEL_FLAG_NORMAL}



	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.major_sort= #{majorSort}


		AND a.sort_id= #{sortId}


		AND (b.city_id = #{cityId} OR a.all_city = '1')




		ORDER BY ${page.orderBy}


		ORDER BY a.sort_num ASC

select id,name,unit,meterage,price from ser_item_commodity
where item_id=#{reqId}
and del_flag = '0'

select city_name as cityName from ser_item_city
where item_id=#{reqId}
and del_flag = '0'

SELECT 

FROM ser_item_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ser_item_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) FROM ser_item_info
WHERE name = #{name}
AND office_id = #{officeId}
AND del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
UPDATE ser_item_info SET

	picture_detail = #{pictureDetail},


	sort_num = #{sortNum},

update_by = #{updateBy.id},
update_date = #{updateDate}
WHERE id = #{id}

mapper.select:
select a.id AS sortId, a.name as sortName
from ser_sort_info a
where del_flag = #{DEL_FLAG_NORMAL}
