mapper.select:
SELECT a.id AS "id", a.type AS "type", a.title AS "title", a.create_by AS "createBy.id", a.create_date AS "createDate", a.remote_addr AS "remoteAddr", a.user_agent AS "userAgent", a.request_uri AS "requestUri", a.method AS method, a.params AS params, a.exception AS exceptions FROM sys_log a
1=1
AND a.type = #{type}
AND a.title LIKE concat('%',#{title},'%')
AND a.create_date >= #{startTime}
AND a.create_date <= #{endTime}
AND a.request_uri LIKE concat('%',#{requestUri},'%')
AND a.params LIKE concat('%',#{params},'%')
ORDER BY a.create_date DESC
