mapper.select:
SELECT
    a.id AS id,
    a.master_id AS masterId,
    a.org_id AS orgId,
    org.name AS orgName,
    a.station_id AS stationId,
    sta.name AS stationName,
    a.order_content AS orderContent,
    a.pay_price AS payPrice,
    a.order_status AS orderStatus,
    a.order_source AS orderSource,
    a.pay_status AS payStatus,
    a.order_time AS orderTime
FROM order_combination_info a
LEFT JOIN basic_organization org ON a.org_id = org.id AND org.del_flag = #{DEL_FLAG_NORMAL}
LEFT JOIN basic_service_station sta ON a.station_id = sta.id AND sta.del_flag = #{DEL_FLAG_NORMAL}

    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.org_id = #{orgId}


        AND a.station_id= #{stationId}


        AND a.order_time >= #{startTime}


        AND a.order_time <= #{endTime}


        AND a.master_id LIKE concat('%',#{masterId},'%')


        AND a.order_content LIKE concat('%',#{orderContent},'%')


${sqlMap.dsf}



            ORDER BY ${page.orderBy}


            ORDER BY a.create_date DESC

SELECT a.id AS "id", a.master_id AS "masterId", a.order_type AS "orderType", a.major_sort AS "majorSort", a.order_status AS "orderStatus", a.order_source AS "orderSource", a.combination_goods_name AS "combinationGoodsName", a.bespeak_total AS "bespeakTotal", a.bespeak_num AS "bespeakNum", a.order_time AS "orderTime", a.service_frequency AS serviceFrequency, a.latitude AS latitude, a.longitude AS longitude, a.origin_price AS originPrice, a.org_id AS "orgId", org.name AS "orgName", a.station_id AS "stationId", sta.name AS "stationName", a.joint_group_id AS "jointGroupId", a.combination_goods_id AS "combinationGoodsId", a.combination_goods_num AS "combinationGoodsNum", a.pay_price AS payPrice, a.pay_status AS "payStatus", pay.pay_number AS "payInfo.payNumber", pay.pay_status AS "payInfo.payStatus", pay.pay_method AS "payInfo.payMethod", pay.pay_platform AS "payInfo.payPlatform", pay.pay_account AS "payInfo.payAccount", pay.pay_time AS "payInfo.payTime",
address.name AS "addressInfo.name", address.phone AS "addressInfo.phone", CONCAT(IFNULL(t1.name,''),IFNULL(t2.name,''),IFNULL(t3.name,''),IFNULL(address.placename,'')) AS "addressInfo.placename", address.detail_address AS "addressInfo.detailAddress", address.address AS "addressInfo.address",
a.service_hour AS "serviceHour", a.service_start AS "serviceStart", a.tech_id AS techId, cust.name AS "customerName", cust.phone AS "customerPhone", a.order_content AS "orderContent", a.cancel_reason AS "cancelReason", a.cancel_reason_remark AS "cancelReasonRemark", tech.name AS "tech.name", tech.phone AS "tech.phone" FROM order_combination_info a LEFT JOIN order_pay_info pay ON a.master_id = pay.master_id AND pay.del_flag = #{DEL_FLAG_NORMAL} LEFT JOIN basic_organization org ON a.org_id = org.id AND org.del_flag = #{DEL_FLAG_NORMAL} LEFT JOIN basic_service_station sta ON a.station_id = sta.id AND sta.del_flag = #{DEL_FLAG_NORMAL}
LEFT JOIN order_address address ON a.order_address_id = address.id AND address.del_flag = #{DEL_FLAG_NORMAL} LEFT JOIN sys_area t1 ON address.province_code = t1.code LEFT JOIN sys_area t2 ON address.city_code = t2.code LEFT JOIN sys_area t3 ON address.area_code = t3.code
LEFT JOIN customer_info cust ON a.customer_id = cust.id AND cust.del_flag = #{DEL_FLAG_NORMAL} LEFT JOIN tech_info tech ON a.tech_id = tech.id AND tech.del_flag = #{DEL_FLAG_NORMAL}
WHERE a.master_id = #{masterId} AND a.del_flag = #{DEL_FLAG_NORMAL}
