FROM customer_info a

WHERE a.id = #{id}

SELECT a.id AS "id", a.name AS "name", a.sex AS "sex", a.phone AS "phone", a.org_id AS "orgId", b.name AS "orgName", a.source AS "source" FROM customer_info a LEFT JOIN basic_organization b ON a.org_id = b.id AND b.del_flag = #{DEL_FLAG_NORMAL}
a.del_flag = #{DEL_FLAG_NORMAL} AND a.source = 'own'
AND a.phone LIKE concat('%',#{phone},'%')
AND a.name LIKE concat('%',#{name},'%')
AND a.org_id = #{orgId}
${sqlMap.dsf}
