mapper.select:
SELECT 

FROM customer_info a

WHERE a.id = #{id}

SELECT a.id AS "id", a.name AS "name", a.sex AS "sex", a.phone AS "phone", a.address AS "address", a.addr_longitude AS "addrLongitude", a.addr_latitude AS "addrLatitude", a.org_id AS "orgId", b.name AS "orgName", a.source AS "source" FROM customer_info a LEFT JOIN basic_organization b ON a.org_id = b.id AND b.del_flag = #{DEL_FLAG_NORMAL}
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.phone LIKE concat('%',#{phone},'%')
AND a.name LIKE concat('%',#{name},'%')
AND a.org_id = #{orgId}
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

SELECT 

FROM customer_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE customer_info SET
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT a.id AS "id", a.name AS "name" FROM basic_organization a
a.del_flag = '0'
${sqlMap.dsf}

SELECT
a.id AS "id"
FROM customer_info a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.org_id = #{orgId}
	AND a.phone = #{phone}
