mapper.select:
SELECT 

FROM order_goods a

WHERE a.id = #{id}

SELECT 

FROM order_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 

FROM order_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE order_goods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
