mapper.select:
SELECT

FROM order_goods a

WHERE a.id = #{id}

SELECT 

FROM order_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 

FROM order_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE order_goods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE order_goods SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE order_id = #{orderId}

UPDATE order_goods SET
	goods_refund_num = goods_num
WHERE order_id = #{orderId}
AND item_id = #{itemId}
AND goods_id = #{goodsId}
AND del_flag = #{DEL_FLAG_NORMAL}

mapper.select:
SELECT

,#{id} AS orderId
FROM ser_item_info a

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.org_id=#{orgId}
	AND a.major_sort=#{majorSort}
	AND LENGTH(a.sort_id) < 3

ORDER BY a.create_date DESC

SELECT
a.id AS id,
a.joint_goods_code AS jointGoodsCode
FROM ser_item_info_goods_eshop a
WHERE a.org_id = #{orgId}
AND a.eshop_code = #{eshopCode}
AND a.goods_id = #{goodsId}
AND a.joint_status = #{jointStatus}
AND a.enabled_status = #{enabledStatus}
