mapper.select:
SELECT a.id AS "id", a.master_id AS "masterId", a.order_type AS "orderType", a.major_sort AS "majorSort", a.order_number AS orderNumber, a.order_status AS "orderStatus", a.service_status AS "serviceStatus", a.order_source AS "orderSource", a.order_time AS "orderTime", a.org_id AS "orgId", org.name AS "orgName", org.visable AS "orgVisable", a.station_id AS "stationId", sta.name AS "stationName", a.joint_order_id AS "jointOrderId", a.pay_price AS payPrice, a.pay_status AS "payStatus", pay.pay_number AS "payInfo.payNumber", pay.pay_status AS "payInfo.payStatus", pay.pay_method AS "payInfo.payMethod", pay.pay_platform AS "payInfo.payPlatform", pay.pay_account AS "payInfo.payAccount", pay.pay_time AS "payInfo.payTime",
address.name AS "addressInfo.name", address.phone AS "addressInfo.phone", CONCAT(IFNULL(t1.name,''),IFNULL(t2.name,''),IFNULL(t3.name,''),IFNULL(address.placename,'')) AS "addressInfo.placename", address.detail_address AS "addressInfo.detailAddress", address.address AS "addressInfo.address",
a.service_time AS "serviceTime", a.finish_time AS "finishTime", a.service_hour AS "serviceHour", a.customer_remark AS "customerRemark", a.customer_remark_pic AS "customerRemarkPic", a.order_remark AS "orderRemark", a.order_remark_pic AS "orderRemarkPic", a.order_content AS "orderContent", a.cancel_reason AS "cancelReason", a.cancel_reason_remark AS "cancelReasonRemark", a.business_name AS "businessName", a.business_phone AS "businessPhone", a.business_remark AS "businessRemark", a.business_remark_pic AS "businessRemarkPic", a.shop_name AS "shopName", a.shop_phone AS "shopPhone", a.shop_addr AS "shopAddr", a.shop_remark AS "shopRemark", a.shop_remark_pic AS "shopRemarkPic" FROM order_info a LEFT JOIN order_pay_info pay ON a.master_id = pay.master_id AND pay.del_flag = #{DEL_FLAG_NORMAL} LEFT JOIN basic_organization org ON a.org_id = org.id AND org.del_flag = #{DEL_FLAG_NORMAL} LEFT JOIN basic_service_station sta ON a.station_id = sta.id AND sta.del_flag = #{DEL_FLAG_NORMAL}
LEFT JOIN order_address address ON a.order_address_id = address.id AND address.del_flag = #{DEL_FLAG_NORMAL} LEFT JOIN sys_area t1 ON address.province_code = t1.code LEFT JOIN sys_area t2 ON address.city_code = t2.code LEFT JOIN sys_area t3 ON address.area_code = t3.code
WHERE a.id = #{id} AND a.del_flag = #{DEL_FLAG_NORMAL}

