mapper.select:
select skill_id
from ser_skill_sort
WHERE sort_id = #{sortId}
and org_id = #{orgId}
and del_flag='0'

SELECT
t.id as techId, t.name AS techName,t.sex AS techSex,t.phone AS techPhone,
t.head_pic AS headPic,t.job_nature AS jobNature
FROM ser_skill_tech a
LEFT JOIN tech_info t ON a.tech_id = t.id AND t.del_flag = #{DEL_FLAG_NORMAL}

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND a.skill_id= #{skillId}
	AND t.station_id= #{stationId}
	AND t.status= #{techStatus}
	AND t.job_status= #{jobStatus}

		AND t.name LIKE concat('%',#{techName},'%')


		AND t.job_nature= #{jobNature}


		AND t.id NOT IN (
			SELECT d.tech_id FROM order_dispatch d
			WHERE d.order_id = #{orderId}
			AND d.status = 'yes' AND d.del_flag = #{DEL_FLAG_NORMAL}
		)


ORDER BY t.create_date DESC

		SELECT
			t.id as techId
		FROM  tech_work_time a
		LEFT JOIN tech_info t ON a.tech_id = t.id AND t.del_flag = #{DEL_FLAG_NORMAL}

			a.del_flag = #{DEL_FLAG_NORMAL}
			AND t.station_id= #{stationId}
			AND a.week =#{week}
			AND a.start_time <= #{startTime}
            AND a.end_time >= #{startTime}

SELECT
t.id as techId
FROM  tech_holiday a
LEFT JOIN tech_info t ON a.tech_id = t.id AND t.del_flag = #{DEL_FLAG_NORMAL}

	a.del_flag = #{DEL_FLAG_NORMAL}
	AND t.station_id= #{stationId}
	AND (
	(a.start_time >= #{startTime} AND a.start_time < #{endTime}) OR
	(a.end_time > #{startTime} AND a.end_time <= #{endTime}) OR
	(a.start_time <= #{startTime} AND a.end_time >= #{endTime}))

SELECT  a.order_id AS "orderId",
a.tech_id AS "techId",
o.service_time AS "serviceTime",
o.finish_time AS "finishTime"
from order_dispatch a
LEFT JOIN order_info o ON a.order_id = o.id AND o.del_flag = #{DEL_FLAG_NORMAL}
WHERE
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.status = 'yes'
AND o.finish_time BETWEEN #{startTime} AND #{endTime}
AND o.finish_time BETWEEN #{startTime} AND #{endTime}
AND (o.service_status='wait_service' or o.service_status='started' or o.service_status='finish')
AND a.tech_id  in

	#{techId}

select
a.tech_id AS "techId",
a.start_time AS "startTime",
a.end_time AS "endTime"
from tech_work_time a
WHERE a.tech_id = #{techId}
AND a.week = #{week}
AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT
a.tech_id AS "techId",
a.start_time AS "startTime",
a.end_time AS "endTime"
from tech_holiday a
WHERE a.tech_id = #{techId}
AND a.start_time BETWEEN #{startTime} AND #{endTime}
AND a.end_time BETWEEN #{startTime} AND #{endTime}
AND a.del_flag = #{DEL_FLAG_NORMAL}

SELECT
a.tech_id AS "techId",
o.id AS "orderId",
o.service_time AS "startTime",
o.finish_time AS "endTime"
from order_dispatch a
LEFT JOIN order_info o ON a.order_id = o.id AND o.del_flag = #{DEL_FLAG_NORMAL}
WHERE
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.status = 'yes'
AND o.service_time BETWEEN #{startTime} AND #{endTime}
AND o.finish_time BETWEEN #{startTime} AND #{endTime}
AND (o.service_status='wait_service' or o.service_status='started' or o.service_status='finish')
AND a.tech_id  = #{techId}

SELECT t1.id AS techId, t1.phone AS techPhone from tech_info t1 LEFT JOIN ( SELECT a.tech_id ,COUNT(1) AS num FROM order_dispatch a LEFT JOIN order_info o ON a.order_id = o.id AND o.del_flag = #{DEL_FLAG_NORMAL} WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND a.status = 'yes' AND o.finish_time BETWEEN #{startTime} AND #{endTime} AND (o.service_status='wait_service' or o.service_status='started' or o.service_status='finish') AND a.tech_id in
#{techId}
GROUP BY a.tech_id ) t2 on t1.id = t2.tech_id WHERE t1.id in
#{techId}
ORDER BY t2.num

