mapper.select:
SELECT 

FROM order_info a
WHERE a.id = #{id}

SELECT

FROM order_info a

	(a.del_flag = #{DEL_FLAG_NORMAL}

	)

		and a.order_number = #{orderNumber}


		and a.station_id = #{stationId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM order_info a

	a.del_flag = #{DEL_FLAG_NORMAL}

		and a.order_number = #{orderNumber}


		and a.station_id = #{stationId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

select p.id as id, p.order_id as orderId, p.pay_mode as payMode, 
	p.pay_time as payTime, p.pay_account as payAccount, p.pay_status as payStatus
		from order_pay_info p
		where p.order_id = #{reqId}

			and p.pay_mode = #{payMode}


			and p.payStatus = #{payStatus}

		and p.del_flag = '0'

select r.id as id, r.order_id as orderId, r.apply_time as applyTime, r.return_account as returnAccount,
	r.return_status as returnStatus, r.return_cause as returnCause, r.return_refuse as returnRefuse
		from order_return r
		where r.order_id = #{reqId}

			and r.return_status = #{returnStatus}

		and r.del_flag = '0'

select i.sort_id as sortId, i.sort_name as sortName, u.* from ((select r.id, r.order_id as orderId, c.item_id as itemId, c.item_name as itemName, c.id as commodityId, c.name as commodityName, c.meterage, c.price, r.order_num as orderNum from order_service_relation r, ser_item_commodity c where c.id = r.item_id and r.order_id = #{reqId}
and c.item_name like concat('%',#{itemName},'%')
and r.del_flag = '0') u left join ser_item_info i on u.itemId = i.id)

mapper.update:
UPDATE order_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
