mapper.select:
SELECT 

FROM order_service_relation a

WHERE a.id = #{id}

SELECT 

FROM order_service_relation a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM order_service_relation a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE order_service_relation SET 
	del_flag = '1'
WHERE id = #{id}
