mapper.update:
UPDATE ser_skill_info SET

		name = #{name},


		code = #{code},


		technician_num = #{technicianNum};

	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE ser_skill_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) FROM ser_skill_info
WHERE name = #{name}
AND  office_id = #{officeId}
AND del_flag = #{DEL_FLAG_NORMAL}

SELECT a.id, a.id AS "itemId", a.name AS "itemName" FROM ser_item_info a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{itemName}||'%' '%'+#{itemName}+'%' concat('%',#{itemName},'%')
ORDER BY ${page.orderBy}
ORDER BY a.sort_num ASC

SELECT a.id as technicianId, a.tech_name AS technicianName,a.tech_sex AS techSex, a.tech_station_id AS techStationId,a.tech_station_name AS techStationName, b.img_url AS imgUrl FROM service_technician_info a LEFT JOIN service_technician_images b ON a.id=b.tech_id AND b.del_flag = #{DEL_FLAG_NORMAL} AND b.img_type = '1'
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.tech_name LIKE '%'||#{technicianName}||'%' '%'+#{technicianName}+'%' concat('%',#{technicianName},'%')
AND a.tech_station_id= #{techStationId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
