mapper.select:
SELECT 

FROM ser_skill_info a

WHERE a.id = #{id}

SELECT a.id AS "id", a.name AS "name", a.code AS "code", a.technician_num AS "technicianNum" FROM ser_skill_info a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM ser_skill_info a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ser_skill_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) FROM ser_skill_info
WHERE name = #{name}
AND  office_id = #{officeId}
AND del_flag = #{DEL_FLAG_NORMAL}

SELECT a.id AS "id", a.name AS "name" FROM ser_item_info a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.sort_num ASC

SELECT a.id, a.tech_name AS techName,a.tech_sex AS techSex, a.tech_station_id AS techStationId,a.tech_station_name AS techStationName, b.img_url AS imgUrl FROM service_technician_info a LEFT JOIN service_technician_images b ON a.id=b.tech_id AND b.del_flag = #{DEL_FLAG_NORMAL} AND b.img_type = '1'
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.tech_name LIKE '%'||#{techName}||'%' '%'+#{techName}+'%' concat('%',#{techName},'%')
AND a.tech_station_id= #{techStationId}
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC
