mapper.select:
SELECT 

FROM ser_skill_goods a

WHERE a.id = #{id}

SELECT 

FROM ser_skill_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 

FROM ser_skill_goods a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE ser_skill_goods SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE ser_skill_goods SET
del_flag = #{DEL_FLAG_DELETE}
WHERE skill_id = #{id}
AND del_flag = #{DEL_FLAG_NORMAL}

mapper.select:
SELECT
a.item_id AS "itemId",
b.name AS "itemName"
FROM
ser_skill_goods a
LEFT JOIN ser_item_info b ON a.item_id = b.id AND b.del_flag = #{DEL_FLAG_NORMAL}
WHERE a.skill_id = #{id}
AND a.del_flag = #{DEL_FLAG_NORMAL}
GROUP BY a.item_id,b.name

SELECT
a.item_id AS "itemId",
b.name AS "itemName",
a.goods_id AS "goodsId",
c.name AS "goodsName"
FROM
ser_skill_goods a
LEFT JOIN ser_item_info b ON a.item_id = b.id AND b.del_flag = #{DEL_FLAG_NORMAL}
LEFT JOIN ser_item_info_goods c ON a.goods_id = c.id AND c.del_flag = #{DEL_FLAG_NORMAL}
WHERE a.skill_id = #{id}
AND a.del_flag = #{DEL_FLAG_NORMAL}
