mapper.select:
SELECT 

FROM ser_skill_technician a

WHERE a.id = #{id}

SELECT 

FROM ser_skill_technician a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ser_skill_technician a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM ser_skill_technician a


	a.del_flag = #{DEL_FLAG_NORMAL} and a.technician_id = #{id}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ser_skill_technician SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE ser_skill_technician SET
del_flag = #{DEL_FLAG_DELETE}
WHERE skill_id = #{id}
AND  del_flag = #{DEL_FLAG_NORMAL}
