mapper.select:
SELECT 

FROM ser_city_scope a

WHERE a.id = #{id}

SELECT 

FROM ser_city_scope a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT 

FROM ser_city_scope a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE ser_city_scope SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE ser_city_scope SET
del_flag = '1'
WHERE master_id = #{id}
