mapper.select:
SELECT 

FROM ser_sort_city a

WHERE a.id = #{id}

SELECT 

FROM ser_sort_city a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM ser_sort_city a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT

FROM ser_sort_city a

	a.sort_id = #{id}
	AND a.del_flag = #{DEL_FLAG_NORMAL}

mapper.update:
UPDATE ser_sort_city SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
