mapper.select:
SELECT 

FROM ser_sort_info a

WHERE a.id = #{id}

SELECT
	a.id AS "id",
	a.major_sort AS "majorSort",
	a.name AS "name"
FROM ser_sort_info a

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE concat('%',#{name},'%')


		AND a.major_sort= #{majorSort}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT a.id AS "id", a.major_sort AS "majorSort", a.name AS "name" FROM ser_sort_info a
a.del_flag = #{DEL_FLAG_NORMAL}
ORDER BY a.major_sort DESC

SELECT COUNT(*) FROM ser_sort_info
WHERE name = #{name}
AND del_flag = #{DEL_FLAG_NORMAL}
AND major_Sort= #{majorSort}

SELECT COUNT(*) FROM ser_sort_info
WHERE name = #{name}
AND major_Sort= #{majorSort}
AND del_flag = #{DEL_FLAG_NORMAL}
AND id!=#{id}

mapper.update:
UPDATE ser_sort_info SET

		major_sort = #{majorSort},


		name = #{name},

	update_by = #{updateBy.id},
	update_date = #{updateDate}
WHERE id = #{id}

UPDATE ser_sort_info SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT COUNT(*) FROM ser_item_info s
WHERE s.sort_id = #{id}
AND s.del_flag = #{DEL_FLAG_NORMAL}

SELECT a.sort_id
from ser_skill_sort a

	a.del_flag = #{DEL_FLAG_NORMAL}
