mapper.select:
SELECT

FROM basic_service_station a

WHERE a.id = #{id}

SELECT

,bo.scope_type AS "organ.scopeType"
,sa.name AS "cityName"
FROM basic_service_station a

LEFT JOIN basic_organization bo on a.org_id = bo.id
LEFT JOIN sys_area sa ON sa.code = a.city_code

    a.del_flag = #{DEL_FLAG_NORMAL}
    ${sqlMap.dsf}

        AND a.name LIKE concat('%',#{name},'%')


        AND a.province_code = #{provinceCode}


        AND a.city_code = #{cityCode}


        AND a.area_code = #{areaCode}


        AND a.org_id = #{orgId}




        ORDER BY ${page.orderBy}


        ORDER BY a.create_date DESC

SELECT

FROM basic_service_station a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE basic_service_station
name = #{name}, type = #{type}, province_code = #{provinceCode}, city_code = #{cityCode}, area_code = #{areaCode}, address = #{address}, user_id = #{userId}, phone = #{phone}, employees = #{employees}, tech_num = #{techNum}, service_point = #{servicePoint}, org_id = #{orgId}, is_useable = #{isUseable}, update_by = #{updateBy.id}, update_date = #{updateDate},
WHERE id = #{id}

UPDATE basic_service_station SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
