mapper.select:
SELECT

FROM basic_service_station a

WHERE a.id = #{id}

SELECT a.id AS "id", a.name AS "name", a.type AS "type", a.province_code AS "provinceCode", a.city_code AS "cityCode", a.area_code AS "areaCode", a.address AS "address", a.phone AS "phone", a.employees AS "employees", a.tech_num AS "techNum", a.service_point AS "servicePoint", a.org_id AS "orgId", a.is_useable AS "isUseable", a.create_by AS "createBy.id", a.create_date AS "createDate", a.update_by AS "updateBy.id", a.update_date AS "updateDate", a.del_flag AS "delFlag", u8.id AS "user.id", u8.name AS "user.name" ,bo.scope_type AS "organ.scopeType" ,sa.name AS "cityName" FROM basic_service_station a LEFT JOIN sys_user u8 ON u8.id = a.user_id and u8.del_flag='0' and a.id=u8.station_id LEFT JOIN basic_organization bo on a.org_id = bo.id LEFT JOIN sys_area sa ON sa.code = a.city_code
a.del_flag = #{DEL_FLAG_NORMAL} ${sqlMap.dsf}
AND a.name LIKE concat('%',#{name},'%')
AND a.province_code = #{provinceCode}
AND a.city_code = #{cityCode}
AND a.area_code = #{areaCode}
AND a.org_id = #{orgId}
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

SELECT

FROM basic_service_station a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.create_date DESC

mapper.update:
UPDATE basic_service_station
name = #{name}, type = #{type}, province_code = #{provinceCode}, city_code = #{cityCode}, area_code = #{areaCode}, address = #{address}, user_id = #{userId}, phone = #{phone}, employees = #{employees}, tech_num = #{techNum}, service_point = #{servicePoint}, org_id = #{orgId}, is_useable = #{isUseable}, update_by = #{updateBy.id}, update_date = #{updateDate},
WHERE id = #{id}

UPDATE basic_service_station SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
        select
			a.station_id AS id
		from basic_service_store a
		LEFT JOIN basic_service_station b on a.station_id = b.id
		WHERE a.store_id = #{storeId}
		AND b.org_id = #{orgId}
