                AND a.store_name LIKE
                '%'||#{storeName}||'%'
                '%'+#{storeName}+'%'
                concat('%',#{storeName},'%')
            
            
                AND a.white = #{white}
            
            
                AND a.province_code = #{provinceCode}
            
            
                AND a.city_code = #{cityCode}
            
            
                AND a.area_code = #{areaCode}
             
            
            AND a.id NOT IN 
            
            #{item}  
         	
         	 
        GROUP BY a.id
        
            
                ORDER BY ${page.orderBy}
            
            
                ORDER BY a.create_date DESC

    SELECT
    
    FROM basic_store a
    
