        a.del_flag = #{DEL_FLAG_NORMAL}
        
            AND a.org_id = #{orgId}
        
        
            AND a.store_id = #{storeId}
        
        
            AND a.store_name LIKE
            '%'||#{storeName}||'%'
            '%'+#{storeName}+'%'
            concat('%',#{storeName},'%')
        
        
