mapper.select:
SELECT 

FROM service_station a

WHERE a.id = #{id}

SELECT 

FROM service_station a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		and a.office_id = #{officeId}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM service_station a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE service_station SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
