mapper.select:
SELECT

FROM basic_service_station a

WHERE a.id = #{id}

SELECT

FROM basic_service_station a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.name LIKE concat('%',#{name},'%')


        and a.office_id = #{officeId}


        and a.addr_city_id = #{addrCityId}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM basic_service_station a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE basic_service_station
update_by = #{updateBy.id}, office_id = #{office.id}, user_id = #{user.id}, user_name = #{userName}, update_by = #{updateBy.id}, phone = #{phone}, name = #{name}, type = #{type}, address = #{address}, remarks = #{remarks}, addr_province_name = #{addrProvinceName}, addr_city_name = #{addrCityName}, addr_district_name = #{addrDistrictName}, addr_detail_info = #{addrDetailInfo}, radius = #{radius}, circle_center = #{circleCenter}, range_type = #{rangeType}, useable = #{useable}, employees = #{employees}, aunts = #{aunts}, service_point = #{servicePoint}, addr_city_id = #{addrCityId}, update_date = #{updateDate}, addr_district_id = #{addrDistrictId}, office_name = #{officeName}, addr_province_id = #{addrProvinceId},
WHERE id = #{id}

UPDATE basic_service_station SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
