mapper.select:
SELECT 

FROM tech_holiday a

WHERE a.id = #{id}

SELECT
	a.id AS "id",
	a.tech_id AS "techId",
	b.name AS "techName",
	b.phone AS "techPhone",
	c.name AS "techStationName",
	a.start_time AS "startTime",
	a.end_time AS "endTime",
	a.remark AS "remark"
FROM tech_holiday a
LEFT JOIN tech_info b ON a.tech_id = b.id
LEFT JOIN basic_service_station c ON c.id = b.station_id

	a.del_flag = #{DEL_FLAG_NORMAL}

		AND b.name LIKE concat('%',#{techName},'%')


		AND b.phone = #{techPhone}


		AND a.start_time >= #{startTime}


		AND a.end_time <= #{endTime}


	${sqlMap.dsf}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM tech_holiday a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE tech_holiday SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
select count(*)
from order_info a
left join order_dispatch b on a.id = b.order_id
where a.del_flag = #{DEL_FLAG_NORMAL} and b.del_flag = #{DEL_FLAG_NORMAL}
and b.tech_id = #{techId}
and a.service_time BETWEEN #{startTime} AND #{endTime}
and (a.order_status = 'dispatched' or a.order_status = 'started')

select count(*)
from tech_holiday a
where a.del_flag = #{DEL_FLAG_NORMAL}
and a.tech_id = #{techId}
and (a.start_time BETWEEN #{startTime} AND #{endTime} OR
a.end_time BETWEEN #{startTime} AND #{endTime} )
