mapper.select:
SELECT 

FROM tech_holiday a

WHERE a.id = #{id}

SELECT a.id AS "id", a.tech_id AS "techId", b.name AS "techName", b.phone AS "techPhone", c.name AS "techStationName", a.start_time AS "startTime", a.end_time AS "endTime", a.remark AS "remark" FROM tech_holiday a LEFT JOIN tech_info b ON a.tech_id = b.id LEFT JOIN basic_service_station c ON c.id = b.station_id
a.del_flag = #{DEL_FLAG_NORMAL}
AND b.name LIKE concat('%',#{techName},'%')
AND b.phone LIKE concat('%',#{techPhone},'%')
AND a.start_time >= #{startTime}
AND a.end_time <= #{endTime}
${sqlMap.dsf}
ORDER BY ${page.orderBy}
ORDER BY a.start_time DESC

SELECT a.id AS "id", a.tech_id AS "techId", b.name AS "techName", b.phone AS "techPhone", a.start_time AS "startTime", a.end_time AS "endTime", a.remark AS "remark", a.review_status AS reviewStatus, a.fail_reason AS failReason, a.source AS source FROM tech_holiday a LEFT JOIN tech_info b ON a.tech_id = b.id
a.del_flag = #{DEL_FLAG_NORMAL} AND a.tech_id=#{techId}
ORDER BY ${page.orderBy}
ORDER BY a.start_time DESC

SELECT 

FROM tech_holiday a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
