mapper.select:
SELECT

FROM service_technician_images a

WHERE a.id = #{id}

SELECT

FROM service_technician_images a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM service_technician_images a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM service_technician_images a


    a.del_flag = #{DEL_FLAG_NORMAL}
    and a.tech_id = #{id}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
