mapper.select:
SELECT  count(*) 
	FROM tech_info a
	WHERE
		del_flag = #{DEL_FLAG_NORMAL}
		AND phone = #{phone} AND id !=#{id}
		AND org_id = #{orgId}

SELECT
	
	FROM tech_info a
	WHERE
		a.del_flag = #{DEL_FLAG_NORMAL}
		AND a.phone = #{phone} AND a.org_id = #{orgId}
