SELECT

FROM service_technician_info a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM service_technician_info a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM service_technician_info a



        a.del_flag = #{DEL_FLAG_NORMAL}


        AND a.tech_phone = #{techPhone}


        AND a.tech_name = #{techName}

mapper.update:
