mapper.select:
SELECT 

FROM service_technician_work_time a

WHERE a.id = #{id}

SELECT 

FROM service_technician_work_time a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM service_technician_work_time a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE service_technician_work_time SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
