FROM sys_area a



	  AND id = #{id}


	  AND code = #{code}

SELECT

FROM sys_area a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

${sqlMap.dsf}
OR a.id = #{currentUser.organization.area.id}
