mapper.select:
SELECT

FROM sys_menu a

WHERE a.del_flag = #{DEL_FLAG_NORMAL}

    and a.type=#{type}

ORDER BY a.sort

SELECT DISTINCT
FROM sys_menu a LEFT JOIN sys_menu p ON p.id = a.parent_id JOIN sys_role_menu rm ON rm.menu_id = a.id JOIN sys_role r ON r.id = rm.role_id JOIN sys_user_role ur ON ur.role_id = r.id JOIN sys_user u ON u.id = ur.user_id AND u.id = #{userId} WHERE a.del_flag = #{DEL_FLAG_NORMAL} AND r.del_flag = #{DEL_FLAG_NORMAL} AND u.del_flag = #{DEL_FLAG_NORMAL}
and a.type=#{type}
AND a.permission IN ( 'order','order_view','order_info','order_time','order_dispatch','order_addTech', 'dispatch','dispatch_view','dispatch_insert','dispatch_info', 'user','user_view','user_insert','user_update','user_delete', 'role','role_view','role_insert','role_update','role_delete', 'class','class_view','class_insert','class_update','class_delete', 'office','office_view','office_insert','office_update', 'clean', 'service', 'basic', 'combination','combination_view','combination_info','combination_regular','combination_order','combination_remark','combination_subscribe') ORDER BY a.sort

mapper.update:
UPDATE sys_menu SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE sys_menu SET 
	sort = #{sort}
WHERE id = #{id}
