mapper.update:
UPDATE sys_joint_wait SET
num = num + 1
WHERE id = #{id}

mapper.delete:
DELETE FROM ser_item_info_goods_eshop
WHERE eshop_code = #{eshopCode}
AND joint_goods_code = #{jointGoodsCode}

mapper.update:
UPDATE ser_item_info_goods_eshop SET
joint_status = #{jointStatus}
WHERE eshop_code = #{eshopCode}
AND joint_goods_code = #{jointGoodsCode}
